INSERT INTO `spider_comment` VALUES ('831', '2021-06-24 10:37:05', '2837839024', '导游非常好，讲解非常详细，人很很温柔。非常不错的体验，高原还是反应挺难受。九寨沟太美了', '2021-06-15', 'v***1');
